async function fetchRecipes(){try{var{data:e,error:r}=await supabase.from("recipes").select("*").order("created_at",{ascending:!1});if(r)throw r;return e}catch(e){return console.error("Error fetching recipes:",e),[]}}async function deleteRecipe(e){try{var r=(await supabase.from("recipes").delete().eq("recipe_id",e))["error"];if(r)throw r;return!0}catch(e){return console.error("Error deleting recipe:",e),!1}}async function incrementRecipeViews(e){try{var{data:r,error:t}=await supabase.from("recipes").select("views_count").eq("recipe_id",e).single();if(t)throw t;var c=r.views_count||0,i=(await supabase.from("recipes").update({views_count:c+1}).eq("recipe_id",e))["error"];if(i)throw i;return!0}catch(e){return console.error("Error updating recipe views:",e),!1}}